import axios from 'axios';

const endpoint = `${process.env.REACT_APP_SERVER_SERVICE}/api/posts`;

export const createPost = async (content, image) => {
    try {
        const form = new FormData()
        form.append('post', JSON.stringify(content))
        form.append('file', image)
        
        const response = await axios.post(endpoint, form, {
            headers: {
                'Content-Type': 'multipart/form-data',
            },
        })

        return response.data;
       
    } catch (error) {
        console.error(error);
    }
}

export const getPosts = async (limit, offset) => {
    try {
        const response = await axios.get(endpoint, {
            params: {
                limit,
                offset
            }
        })

        return response.data;
       
    } catch (error) {
        console.error(error);
    }
}

export const deletePost = async (postId) => {
    try {
        const response = await axios.delete(`${endpoint}/${postId}`)

        return response.data;
       
    } catch (error) {
        console.error(error);
    }
}
