import React, { useState, useEffect, useRef, useContext } from "react";
import PostComponent from "./Post";
import { getPosts } from "../services/post.service";
import { UserContext } from "../contexts/UserContext";
import InfiniteScroll from "react-infinite-scroll-component";
import socket from "../socket/socket-client";

export default function ListPost() {
    const user = useContext(UserContext);
    const [posts, setPosts] = useState([]);
    const [hasMore, setHasMore] = useState(false)

    const maxPost = useRef(0);
    const limit = 6;
    const offset = 0;

    useEffect(() => {
        fetchMoreData()
    }, [])

    useEffect(() => {
        socket.on("delete-post", (payload) => {
            if (posts.length === 0) return;

            const postId = payload.data;
            const newPosts = posts
                .filter((item) => item._id !== postId)
                .sort((a, b) => b.creationTime - a.creationTime)
                .slice(); // Create a new array to avoid modifying the original array

            setPosts(newPosts);
        });

        socket.on("new-post", (payload) => {
            console.log("payload", payload)
            const data = payload.data;

            // Calc time ago of post.creationTime
            data.creationTime = calcTimeAgo(data.creationTime)

            // Add owner of post
            data.owner = {
                _id: user._id,
                firstName: user.firstName,
                lastName: user.lastName,
                avatar: user.avatar,
            }

            // Add new post to list post
            setPosts((prevPosts) => [data, ...prevPosts].sort((a, b) => b.creationTime - a.creationTime));
        });

        return () => {
            socket.off("delete-post");
            socket.off("new-post");
        };
    }, [posts.length])

    async function fetchMoreData() {
        const payload = await getPosts(limit, offset + maxPost.current)

        if (payload.data.length > 0) {
            // Calc time ago of post.creationTime
            payload.data.map((item) => {
                item.creationTime = calcTimeAgo(item.creationTime)
            })

            // Add new post to list post
            setPosts([...posts, ...payload.data])
            maxPost.current += payload.data.length

            // Check has more post
            setHasMore(true)
        } else {
            setHasMore(false)
        }
    }

    // Calc time ago of post.creationTime
    function calcTimeAgo(creationTime) {
        const date = new Date(creationTime);
        const now = new Date();
        const timeAgo = now - date;

        const second = 1000;
        const minute = second * 60;
        const hour = minute * 60;
        const day = hour * 24;
        const week = day * 7;
        const month = day * 30;
        const year = month * 12;

        if (timeAgo < minute) {
            return "Vừa xong";
        } else if (timeAgo < hour) {
            return Math.floor(timeAgo / minute) + " phút";
        } else if (timeAgo < day) {
            return Math.floor(timeAgo / hour) + " giờ";
        } else if (timeAgo < week) {
            return Math.floor(timeAgo / day) + " ngày";
        } else if (timeAgo < month) {
            return Math.floor(timeAgo / week) + " tuần";
        } else if (timeAgo < year) {
            return Math.floor(timeAgo / month) + " tháng";
        } else {
            return Math.floor(timeAgo / year) + " năm";
        }
    }

    return (
        <InfiniteScroll
            dataLength={posts.length}
            next={fetchMoreData}
            hasMore={hasMore}
            loader={<h4>Loading...</h4>}
            scrollableTarget="content"
        >
            {
                posts.map((item, index) => (
                    <PostComponent key={index} post={item} />
                ))
            }
        </InfiniteScroll>
    );
}
