import { useContext, useEffect, useState } from 'react'
import { UserContext } from '../contexts/UserContext'
import CommentList from './CommentList'
import FormComment from './FormComment';
import { addReaction, removeReaction } from '../services/reaction.service';
import { deletePost, getPost } from '../services/post.service';

import '../styles/post.css'
import socket from '../socket/socket-client';

export default function Post({ _id }) {
    const domain = process.env.REACT_APP_SERVER_SERVICE;
    const [isActiveTooltip, setIsActiveTooltip] = useState(false)
    const user = useContext(UserContext);
    
    // const existImage = (image !== "") ? true : false
    const [totalLove, setTotalLove] = useState(0)
    const [isLove, setIsLove] = useState(false)
    const [post, setPost] = useState({
        owner: {
            firstName: '',
            lastName: '',
            avatar: ''
        },
        creationTime: '',
        image: '',
        loves: []
    });
    const [existImage, setExistImage] = useState(false)

    // Get info post
    useEffect(() => {
        async function getPostInfo() {
            const payload = await getPost(_id)
            
            if (payload.code === 200) {
                const data = payload.data
                setPost(data)
                setTotalLove(data.loves.length)
                console.log(payload.data)
                if (data.image !== "") {
                    setExistImage(true)
                } else {
                    setExistImage(false)
                }
            }
        }

        getPostInfo()
    }, [])

    // Nếu ấn bất kỳ đâu mà không phải là tooltip và isActiveTooltip là true thì sẽ tắt tooltip 
    useEffect(() => {
        function handleEventScreen(event) {
            if (event.target.closest('.more')) {
                return;
            }
            if (isActiveTooltip) {
                setIsActiveTooltip(false)
            }
        }
        document.addEventListener('click', handleEventScreen)
        return () => {
            document.removeEventListener('click', handleEventScreen)
        }
    }, [isActiveTooltip])

    function activeTooltips() {
        if (isActiveTooltip === false)
            setIsActiveTooltip(true);
        else
            setIsActiveTooltip(false);
    }

    async function handleDeletePost() {
        const payload = await deletePost(_id)
    }

    async function handleEmoji() {
        if (isLove === false) {
            const payload = await addReaction(_id, user._id)

            if (payload.code === 200)
                setIsLove(true)
        } else {
            const payload = await removeReaction(_id, user._id)

            if (payload.code === 200)
                setIsLove(false)
        }
    }

    function calcTimeAgo(creationTime) {
        const date = new Date(creationTime);
        const now = new Date();
        const timeAgo = now - date;

        const second = 1000;
        const minute = second * 60;
        const hour = minute * 60;
        const day = hour * 24;
        const week = day * 7;
        const month = day * 30;
        const year = month * 12;

        if (timeAgo < minute) {
            return "Vừa xong";
        } else if (timeAgo < hour) {
            return Math.floor(timeAgo / minute) + " phút";
        } else if (timeAgo < day) {
            return Math.floor(timeAgo / hour) + " giờ";
        } else if (timeAgo < week) {
            return Math.floor(timeAgo / day) + " ngày";
        } else if (timeAgo < month) {
            return Math.floor(timeAgo / week) + " tuần";
        } else if (timeAgo < year) {
            return Math.floor(timeAgo / month) + " tháng";
        } else {
            return Math.floor(timeAgo / year) + " năm";
        }
    }

    return (
        <div className="post">
            <div className="row justify-between">
                {/* LEFT */}
                <div className="row align-items">
                    <img
                        className="avatar"
                        src={`${domain}/usera/${post.owner.avatar}`}
                        alt=""
                    />

                    <div className="ml-10">
                        <p className="user">{post.owner.firstName} {post.owner.lastName}</p>
                        <p className="time">{calcTimeAgo(post.creationTime)}</p>
                    </div>
                </div>
                {/* RIGHT */}
                <ul className="list-icon row">
                    <li>
                        <i className="fa-solid fa-bookmark" />
                    </li>
                    <li className='more' onClick={activeTooltips}>
                        <i className="fa-solid fa-ellipsis-vertical" />

                        {/* MORE OPTIONS */}
                        <ul className={(isActiveTooltip) ? 'tooltips tooltips-active' : 'tooltips'}>
                            <li className={
                                (2 !== user._id) ? 'disable' : ''
                            }
                                onClick={handleDeletePost}><span className="material-symbols-outlined">delete</span>Delete This Post</li>

                            <li><span className="material-symbols-outlined">delete</span>Add Bookmark</li>
                        </ul>
                    </li>
                </ul>
            </div>
            <p className="text">
             
            </p>
            <div className={`list-image ${existImage ? 'box-active' : 'box-disable'}`}>

                <div style={{
                    backgroundImage: `url(${domain}/posts/${post.image})`
                }}

                    className='boxReview-image'></div>
            </div>
            {/* Emoji */}
            <div className="emoji">
                <div className='no-select' onClick={handleEmoji}>
                    <i className={`fa-regular fa-heart ${(isLove) ? 'shape-effect__love' : ''} `} />
                    <span>{totalLove}</span>
                </div>
                <div className='no-select'>
                    <i className="fa-regular fa-comment" />
                    <span>Comment</span>
                </div>
                <div className='no-select'>
                    <i className="fa-solid fa-share" />
                    <span>Share</span>
                </div>
            </div>
            {/* Form Comment */}
            <FormComment />

            {/* Group Comment */}
            {/* <CommentList comments={comments} /> */}
        </div>
    )
}
