import { useEffect, useState } from "react"

const server = process.env.REACT_APP_SEVER_IP + ":" + process.env.REACT_APP_SERVER_PORT

/**
* Message component
* @param {Number} owner
* @param {Object} chat
* @param {Number} currentUser
*/
export default function Message({ owner, chat, currentUser }) {
    const [avatar, setAvatar] = useState("")

    useEffect(() => {
        if (chat.sender._id === currentUser._id) {
            const avatar = currentUser.avatar
            setAvatar(server + "/uploads/" + avatar)
        } else {
            const avatar = chat.sender.avatar
            setAvatar(server + "/uploads/" + avatar)
        }
    }, [chat, currentUser])

    return (
        <div className={`comment ${ (owner === "other") ? "comment-left" : "comment-right"}`}>
            <img
                className="avatar"
                src={avatar}
                alt="avatar"
            />
            <div className="box">
                <p className="text">{chat.message.text}</p>
            </div>
        </div>
    )
}
